import { useState, useEffect} from 'react'
import Loop from './Loop'
import {Button, Container, Stack, Row, Col, CloseButton, Text, Form} from 'react-bootstrap';


function LoopList(){
    const [listItems, setListItems] = useState([<Loop></Loop>,<Loop></Loop>])
    const [showForm, setShowForm] = useState(false)

    const submitLoop = () => {
        setShowForm(false)
        console.log(typeof(listItems))
        setListItems(listItems => [...listItems,<Loop></Loop>])
    }

    const addLoop = () => {
        setShowForm(true)
    }
    return (
      <Container>
        {!showForm && (
          <ul>
            {listItems}
            <Button onClick={addLoop}></Button>
          </ul>
        )}
        {showForm && (
          <Form>
            <Form.Group className="mb-3">
              <Form.Label>Title</Form.Label>
              <Form.Control placeholder="Loop 1, Loop 2..." />
            </Form.Group>
            <Row className="mb-3">
              <Form.Group as={Col}>
                <Form.Label>Start Time</Form.Label>
                <Form.Control/>
              </Form.Group>

              <Form.Group as={Col}>
                <Form.Label>End Time</Form.Label>
                <Form.Control/>
              </Form.Group>
            </Row>
            <Button onClick={submitLoop}></Button>
          </Form>
        )}
      </Container>
    );
}
export default LoopList;
