    const [listItems, setListItems] = useState([<Loop></Loop>,<Loop></Loop>])
    const [showForm, setShowForm] = useState(false)

    const submitLoop = () => {
        setShowForm(false)
        console.log(typeof(listItems))
        setListItems(listItems.push(<Loop></Loop>))
    }

    const addLoop = () => {
        setShowForm(true)
    }
    return (

        <Container>
            {!showForm && (
            <ul>
                {listItems}
                <Button onClick={addLoop}></Button>
            </ul>)
            }
            {showForm && (
            <Form>
                <p>FORM</p>
                <Button onClick={submitLoop}></Button>
            </Form>
            )
            }
        </Container>
    );
}
export default LoopList;
